#include <bits/stdc++.h>
using namespace std;

template<typename T>
ostream& operator<<(ostream& os, const vector<T>& xs) {

    if (xs.size() == 0) { 
        os << "{}";
        return os;
    }

    os << "{" << xs.at(0);
    for (size_t ii{1}; ii < xs.size(); ++ii) { os << "," << xs.at(ii); }
    os << "}";
    return os;
}

class Solution {
public:
    int numMatchingSubseq(const string& s, const vector<string>& words) {
        
        vector<vector<size_t>> letters(26);
        for (size_t ii{0}; ii < s.size(); ++ii) { letters[s.at(ii)-'a'].push_back(ii); }
        
        // For each word, consider the starting letter,
        // and the indices of said letter in s.
        //
        int result{0};
        for (const string& word : words) {

            // Check starting letter
            if (letters[word.at(0)-'a'].size() == 0) { continue; }
            
            vector<size_t> inds(26,0); // indices for each letter
            size_t pos{0}; // currently at s.at(pos)
            bool ok{true};

            for (size_t ii{0}; ii < word.size(); ++ii) {
                
                const char ch{word.at(ii)};
                while (inds[ch-'a'] < letters[ch-'a'].size()) {
                    if (letters[ch-'a'].at(inds[ch-'a']) >= pos) { break; }
                    ++inds[ch-'a'];
                }
                if (inds[ch-'a'] >= letters[ch-'a'].size()) { // word not viable
                    ok = false;
                    break;
                }
                pos = letters[ch-'a'].at(inds[ch-'a'])+1;
            }
            
            if (ok) { ++result; }
        }
        
        return result;
    }
};

struct Testcase {

    const string _s;
    const vector<string> _words;
    const int _expected;

    void info() const {
        cout << "s:" << _s << " _words:" << _words.size() << " _expected:" << _expected << endl;
    }

    void test() const {

        info();
        Solution soln;
        int actual{soln.numMatchingSubseq(_s, _words)};
        cout << "actual:" << actual << endl;
        assert(_expected == actual);
    }
};

